function addToMyTimety(e,t){if(t){var n=jQuery('.akt_tkvm[id="'+e+'"]');if(!n.length){jQuery.ajax({type:"POST",url:TIMETY_PAGE_AJAX_GETEVENT,data:{eventId:e},success:function(e){var t=jQuery.parseJSON(e);if(t){var n=jQuery("<div>");n.attr("id",t.id);n.attr("class","akt_tkvm");n.attr("time",t.startDateTimeLong);var r=jQuery("<h1>");r.text(t.title);n.append(r);var i=jQuery("<p>");var s=t.startDateTime;try{s=getLocalTime(s).format("HH:mm")}catch(o){s=e.startDateTime}i.append("Today @"+"<span class='date_timezone'>"+s+"</span>");n.append(i);var u=jQuery("#slides_container");var a=u.children();var f=false;for(var l=0;l<a.length;l++){var c=jQuery(a[l]).attr("time")+"";var h=t.startDateTimeLong;if(c>h){n.insertBefore(a[l]);f=true;break}}if(!f){u.append(n);f=true}checkMyTimety();resizeSlide()}}},"json")}}}function removeFromMyTimety(e){jQuery("#"+e).remove();checkMyTimety();resizeSlide()}function checkMyTimety(){var e=jQuery("#slides_container");if(e&&e.length>0){var t=jQuery(e).children();if(t&&t.length>1){jQuery("#create_event_empty").hide()}else{jQuery("#create_event_empty").show()}}}