function addToMyTimety(e,t){if(t){var n=jQuery('.akt_tkvm[id="'+e+'"]');if(!n.length){jQuery.ajax({type:"POST",url:TIMETY_PAGE_AJAX_GETEVENT,data:{eventId:e},success:function(e){var t=jQuery.parseJSON(e);if(t){var n=jQuery("<div>");n.attr("id",t.id);n.attr("class","akt_tkvm");n.attr("time",t.startDateTimeLong);var r=jQuery("<h1>");r.text(t.title);n.append(r);var i=jQuery("<p>");i.text(t.startDateTime);n.append(i);var s=t.description;if(s.length>55){s=t.description.substring(0,55)}var o=jQuery("<p>");o.text(s);n.append(o);var u=jQuery("#slides_container");var a=u.children();var f=false;for(var l=0;l<a.length;l++){var c=jQuery(a[l]).attr("time")+"";var h=t.startDateTimeLong;if(c>h){n.insertBefore(a[l]);f=true;break}}if(!f){u.append(n);f=true}resizeSlide()}}},"json")}}}function removeFromMyTimety(e){jQuery("#"+e).remove()}