function addToMyTimety(e,t){if(t){var n=jQuery('.akt_tkvm[id="'+e+'"]');if(!n.length){jQuery.ajax({type:"POST",url:TIMETY_PAGE_AJAX_GETEVENT,data:{eventId:e},success:function(e){var t=jQuery.parseJSON(e);if(t){var n=jQuery("<div>");n.attr("id",t.id);n.attr("class","akt_tkvm");n.attr("time",t.startDateTimeLong);var r=jQuery("<h1>");r.text(t.title);n.append(r);var i=jQuery("<p>");var s=t.startDateTime;try{s=getLocalTime(s).format("YYYY-MM-DD HH:mm")}catch(o){s=e.startDateTime}i.text(s);n.append(i);var u=t.description;if(u.length>55){u=t.description.substring(0,55)}var a=jQuery("<p>");a.text(u);n.append(a);var f=jQuery("#slides_container");var l=f.children();var c=false;for(var h=0;h<l.length;h++){var p=jQuery(l[h]).attr("time")+"";var d=t.startDateTimeLong;if(p>d){n.insertBefore(l[h]);c=true;break}}if(!c){f.append(n);c=true}checkMyTimety();resizeSlide()}}},"json")}}}function removeFromMyTimety(e){jQuery("#"+e).remove();checkMyTimety();resizeSlide()}function checkMyTimety(){var e=jQuery("#slides_container");if(e&&e.length>0){var t=jQuery(e).children();if(t&&t.length>1){jQuery("#create_event_empty").hide()}else{jQuery("#create_event_empty").show()}}}