function setTooltip(){jQuery(".likeshare").each(function(e,t){setTooltipLikeShareDiv(t)})}function setTooltipButton(e,t){if(e){e=jQuery(e);e.tooltip("destroy");if(t){jQuery(e).attr("data-original-title",t);jQuery(e).tooltip()}}}function setTooltipLikeShareDiv(e){if(e){var t=jQuery(e).find(".like_btn");setTooltipButton(t,getLanguageText("LANG_SOCIAL_LIKE"));var n=jQuery(e).find(".like_btn_aktif");setTooltipButton(n,getLanguageText("LANG_SOCIAL_UNLIKE"));var r=jQuery(e).find(".maybe_btn");setTooltipButton(r,getLanguageText("LANG_SOCIAL_MAYBE"));var i=jQuery(e).find(".maybe_btn_aktif");setTooltipButton(i,getLanguageText("LANG_SOCIAL_DECLINE"));var s=jQuery(e).find(".share_btn");setTooltipButton(s,getLanguageText("LANG_SOCIAL_RESHARE"));var o=jQuery(e).find(".share_btn_aktif");setTooltipButton(o,getLanguageText("LANG_SOCIAL_REVERT"));var u=jQuery(e).find(".join_btn");setTooltipButton(u,getLanguageText("LANG_SOCIAL_JOIN"));var a=jQuery(e).find(".join_btn_aktif");setTooltipButton(a,getLanguageText("LANG_SOCIAL_DECLINE"));var f=jQuery(e).find(".edit_btn");setTooltipButton(f,getLanguageText("LANG_SOCIAL_EDIT_EVENT"))}}jQuery(document).ready(function(){setTooltip()})